const total = loot.reduce((a,x) => a+x.value, 0);
const totalStr = total.toFixed(2);
const lootTable = loot.map(x => ({
  ...x,
  value: x.value.toFixed(2),
  percentage: (x.value / total * 100).toFixed(1)
})).sort((x,y) => y.percentage - x.percentage);

